/**
 * Returns command line arguments as an associative array
 * 
 * @param argv
 * @returns {Array}
 */
var getOpts = function (argv) {
	var opts = [], hasArg = false, lastVal;
	argv.forEach(function (val, idx) {
		if (val.match(/^-/) && hasArg == false) {
			lastVal = val.replace('-','');
			hasArg = true;
		}else if (hasArg == true) {
			opts[lastVal] = val;
			hasArg = false;
		}
	});
	return opts;
}
// ex. var cmdopts = getOpts(process.argv);

module.exports = getOpts;